import React, { useState, useEffect, PropTypes  } from "react";
import {Routes, Route} from 'react-router';
import { withRouter, Redirect } from "react-router-dom";


import Button from '@material-ui/core/Button';
import './App.css';
import WeatherSummary from './WeatherSummary.js';
import ResponsiveAppBar from "./ResponsiveAppBar.js";
import ProductDisplay from "./ProductDisplay";
import Feature from "./Feature";
import {TransitionGroup, CSSTransition} from 'react-transition-group';
import CSSTransitionGroup from 'react-transition-group/CSSTransitionGroup';
import AuthForm from "./AuthForm";
import { authUser } from "./auth.js";


//import './css/login.css';





class App extends React.Component {

  constructor(props) {
    super(props);
    this.state = {
        isVisible: true
      }
  }


  render() {

     // const for React CSS transition declaration
     let component = this.state.isVisible ? <Modal onSubmit={ this.handleSubmit } key='modal'/> : <ModalBack onClick={ this.handleRemount } key='bringitback'/>;

  return (
<div>
<ResponsiveAppBar/>
<Routes>
  <Route path="Products" element=
  { 
      <CSSTransitionGroup transitionName="animation" transitionAppear={true} transitionAppearTimeout={500} transitionEnterTimeout={500} transitionLeaveTimeout={300}>
      { component }
      </CSSTransitionGroup>
  } />
<Route
      path="Pricing"
          element={
             <AuthForm
            onAuth={authUser}
            buttonText="Log in"
            heading="Welcome Back." /> }
        />
</Routes>
<ProductDisplay />
<Feature />


</div>
  );
}}




export default App;
